import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css" ;

class Todo extends Component {

    constructor(props)
    {
        super(props);
        this.state = {
            tasks : [
                {id : "1" , task :"Buy Groceries for Next week"} ,
                {id : "2" , task : "Renew Car Insurrance" },
                {id : "3" , task: "Sign up for online course"}
            ]
        }
    }
    render() {
        return (
            <div>
                <table class="table">
                    <thead>
                        <tr>
                        <th scope="col">id</th>
                        <th scope="col">Tasks</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.tasks.map((t,index)=>
                            <tr key={t.id}>
                            <td>{t.task}</td>
                            <td><button type="button" class="btn btn-primary"><i class="far fa-eye"></i></button></td>
                            </tr>
                        )}
                    </tbody>
                </table>               
            </div>
        );
    }
}

export default Todo;
