  import React, { Component } from 'react';
  import "bootstrap/dist/css/bootstrap.min.css";

  class Todo extends Component {

  constructor(props) {
    super(props);
    this.state = {
      taskvalue: "",
      tasks: [
        { id: 1, task: "Buy Groceries for Next week" , completed:false },
        { id: 2, task: "Renew Car Insurrance" , completed:false},
        { id: 3, task: "Sign up for online course" , completed : true }
      ]
    }
  }
  add = (event) => {
    event.preventDefault();
    let newObject = {
      id: [...this.state.tasks].pop().id + 1,
      task: this.state.taskvalue,
      completed : false
    }
    this.setState(
      {
        tasks: [...this.state.tasks, newObject]

      }
    )
  }

  handleChange = (e) => {
    let task = e.target.value;
    this.setState(
      {
        taskvalue: task
      }
    )

  }
  delete = (t) => {
    let copiedTasks = [...this.state.tasks];
    let index = copiedTasks.findIndex(x => x.id === t.id);
    copiedTasks.splice(index, 1);
    this.setState(
      {
        tasks: copiedTasks
      }
    )
  }

  clearAllTask = () => {
    this.setState(
      {
        tasks : []
      }
    )
  }


  filterTasks = (showCompleted) => {
    const filteredTasks = this.state.tasks.filter(task => {
      return showCompleted ? task.completed : !task.completed;
    });

    this.setState({
      tasks: filteredTasks
    });
  }

  toggleTaskCompletion = (taskId) => {
    const updatedTasks = this.state.tasks.map(task => {
      if (task.id === taskId) {
        return { ...task, completed: !task.completed };
      }
      return task;
    });

    this.setState({
      tasks: updatedTasks
    });
  };

  sortTasksAlphabetically = () => {
    const sortedTasks = [...this.state.tasks].sort((a, b) => a.task.localeCompare(b.task));

    this.setState({
      tasks: sortedTasks
    });
  } 
  render() {
    const incompleteTasksCount = this.state.tasks.reduce((count, task) => !task.completed ? count + 1 : count, 0);
  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Todo App</h1>
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          value={taskValue}
          onChange={(e) => setTaskValue(e.target.value)}
          placeholder="Enter task"
        />
        <button className="btn btn-primary" type="button" onClick={addTask}>Add Task</button>
      </div>
      <ul className="list-group">
        {tasks.map(task => (
          <li key={task.id} className={`list-group-item ${task.completed ? 'list-group-item-secondary' : ''}`}>
            <div className="d-flex justify-content-between align-items-center">
              {task.task}
              <div>
                <button className="btn btn-success mx-1" onClick={() => toggleTaskCompletion(task.id)}>
                  {task.completed ? 'Uncomplete' : 'Complete'}
                </button>
                <button className="btn btn-danger" onClick={() => deleteTask(task.id)}>Delete</button>
              </div>
            </div>
          </li>
        ))}
      </ul>
      <div className="mt-3">
        <button className="btn btn-secondary mx-1" onClick={sortTasksAlphabetically}>Sort Alphabetically</button>
        <button className="btn btn-info mx-1" onClick={countIncompleteTasks}>Count Incomplete Tasks</button>
        <button className="btn btn-danger mx-1" onClick={clearAllTasks}>Clear All Tasks</button>
      </div>
    </div>
  );
        }}
  export default Todo;
